from pybricks.hubs import PrimeHub

from micropup import pup_add_command, pup_call, pup_init

# Set up all devices.
prime_hub = PrimeHub()

# Initialize variables.
r = 0
g = 0
b = 0


# The main program starts here.
pup_init('A')
# pup_add_command  <name> <to_hub> <from_hub>
pup_add_command('led', 0, 4)
while True:
    for i in range(100):
        for nr in range(1, 10, 1):
            # generate some colors depending on i and nr
            r = (100 - (i + nr * 3)) % 100
            g = (i + nr) % 100
            b = (i + (50 - nr * 2)) % 100
            # send these colors to the LMS-ESP32
            pup_call('led', nr, r, g, b)
