from pybricks.parameters import Direction, Port, Stop
from pybricks.pupdevices import Motor
from pybricks.tools import multitask, run_task, wait

from micropup import add_command, call_multitask, init, process_calls

# Set up all devices.
l_leg = Motor(Port.B, Direction.CLOCKWISE)
r_leg = Motor(Port.A, Direction.COUNTERCLOCKWISE)
body = Motor(Port.E, Direction.CLOCKWISE)
head = Motor(Port.F, Direction.CLOCKWISE)

# Initialize variables.
_2 = init('C')
gesture_cmd = add_command('gest', 1, 1)
proximity_cmd = add_command('prox', 1, 0)
stop_cmd = add_command('color', 3, 0)
rgb = 0

async def Neutral():
    await wait(0)
    await body.run_target(300, 0)
    await head.run_target(300, 0)
    await l_leg.run_target(300, 0)
    await r_leg.run_target(300, 0)

async def Walk(steps):
    await wait(0)
    for count in range(int(steps)):
        await wait(0)
        await body.run_target(100, 40)
        await head.run_target(300, -30)
        await l_leg.run_target(100, -20)
        await head.run_target(300, 0)
        await r_leg.run_target(100, 30)
        await body.run_target(100, -40)
        await head.run_target(300, -30)
        await r_leg.run_target(100, -20)
        await head.run_target(300, 0)
        await l_leg.run_target(100, 30)
    await Neutral()

async def main1():
    # Task 1: Follow the hand
    await Neutral()
    while True:
        await wait(0)
        if await call_multitask('gest', 0) == 3:
            # up
            await head.run_angle(500, 20, Stop.COAST)
            await call_multitask('gest', 1)
        if await call_multitask('gest', 0) == 1:
            # up
            await head.run_angle(500, -20, Stop.COAST)
            await call_multitask('gest', 1)
        if await call_multitask('gest', 0) == 2:
            # up
            await body.run_angle(500, 20, Stop.COAST)
            await call_multitask('gest', 1)
        if await call_multitask('gest', 0) == 4:
            # up
            await body.run_angle(500, -20, Stop.COAST)
            await call_multitask('gest', 1)

async def main2():
    # Task 2: Communicate with LMS-ESP32 Microblocks
    await process_calls()

async def main3():
    global rgb
    # Task 3: Walk if the color sensor sees red
    while True:
        await wait(0)
        rgb = await call_multitask('color')
        if rgb[0] > 90 and rgb[0] > rgb[1] and rgb[0] > rgb[2]:
            print('red')
            await Walk(1)
        await wait(400)


async def main():
    await multitask(main1(), main2(), main3())

run_task(main())