from pybricks.parameters import Direction, Port, Stop
from pybricks.pupdevices import Motor
from pybricks.tools import StopWatch

from micropup import add_command, call, init

# Set up all devices.
watch_2 = StopWatch()
l_leg = Motor(Port.B, Direction.CLOCKWISE)
r_leg = Motor(Port.A, Direction.COUNTERCLOCKWISE)
body = Motor(Port.E, Direction.CLOCKWISE)
head = Motor(Port.F, Direction.CLOCKWISE)

# Initialize variables.
rgb = 0

def Neutral():
    body.run_target(300, 0)
    head.run_target(300, 0)
    l_leg.run_target(300, 0)
    r_leg.run_target(300, 0)

def Walk(steps):
    for count in range(int(steps)):
        body.run_target(100, 40)
        head.run_target(300, -30)
        l_leg.run_target(100, -20)
        head.run_target(300, 0)
        r_leg.run_target(100, 30)
        body.run_target(100, -40)
        head.run_target(300, -30)
        r_leg.run_target(100, -20)
        head.run_target(300, 0)
        l_leg.run_target(100, 30)
    Neutral()


# The main program starts here.
init('C')
add_command('gest', 1, 1)
add_command('prox', 1, 0)
add_command('color', 3, 0)
Neutral()
while True:
    if call('gest', 0) == 3:
        # up
        head.run_angle(500, 20, Stop.COAST)
        call('gest', 1)
    if call('gest', 0) == 1:
        # up
        head.run_angle(500, -20, Stop.COAST)
        call('gest', 1)
    if call('gest', 0) == 2:
        # up
        body.run_angle(500, 20, Stop.COAST)
        call('gest', 1)
    if call('gest', 0) == 4:
        # up
        body.run_angle(500, -20, Stop.COAST)
        call('gest', 1)
    if watch_2.time() > 400:
        # Reading the color too often causes the sensor to miss gestures
        watch_2.reset()
        rgb = call('color')
        if rgb[0] > rgb[1] and rgb[0] > rgb[2]:
            print('red')
            Walk(1)
