from pybricks.hubs import PrimeHub

from micropup import add_command, call, init

# Set up all devices.
prime_hub = PrimeHub()


# The main program starts here.
init('A')
# pup_add_command  <name> <to_hub> <from_hub>
add_command('to_hub', 3, 0)
add_command('from_hub', 0, 1)
add_command('to_and_from_hub', 1, 2)
while True:
    print(call('to_hub'))
    call('from_hub', 123)
    print(call('to_and_from_hub', 12, 34))
