from pybricks.hubs import PrimeHub
from pybricks.tools import StopWatch, wait

from micropup import pup_add_command, pup_call, pup_init

# Set up all devices.
prime_hub = PrimeHub()
timer = StopWatch()


# The main program starts here.
print('Hello, Pybricks!')
pup_init('A')
pup_add_command('aap', 1, 1)
pup_add_command('kat', 8, 8)
pup_add_command('muis', 0, 3)
pup_add_command('time', 1, 0)
print('test all commands:')
for item in range(10):
    print(pup_call('aap', item))
    wait(100)
print(pup_call('kat', 1, 2, 3, 4, 5, 6, 7, 8))
timer.reset()
for item in range(500):
    pup_call('muis', 500 - item, item, item * 2)
print(timer.time())
for count in range(10):
    print('time', pup_call('time'))
    wait(100)
